import React from 'react';
import PaystackPop from '@paystack/inline-js';

const Payment = ({
    user,
    checkoutData,
    handleBackStep,
    handleNextStep,
    handleCheckout,
  }) => {

    const paystackPay = (e) => {
        e.preventDefault()
        console.log(checkoutData)

        let psreference = "";

        const orderData = {
            payment: {
              gateway: "paystack",
              paystack: {
                reference: {psreference},
              },
            },
            shipping: {
              name: "stander",
              street: user.address,
              town_city: user.city,
              county_state: user.shippingSubdivision,
              postal_zip_code: user.postcode,
              country: user.shippingCountry,
            },
            customer: {
              firstname: user.firstName,
              lastname: user.lastName,
              email: user.email,
            },
            line_items: checkoutData.live.line_items,
            fulfillment: { shipping_method: user.shippingOptions },
          };


        const paystack = new PaystackPop()
        paystack.newTransaction({
            key: "pk_test_0e824463d20818c6550d20220c1cffb88aac11e6",
            amount: checkoutData.live.subtotal.raw * 100,
            firstname: user.firstName,
            lastname: user.lastName,
            email: user.email,
            onSuccess(transaction){
                let message = `Successfull Reference ${transaction.reference}`
                console.log(message);
                psreference = transaction.reference;
                handleCheckout(checkoutData.id, orderData);
                handleNextStep(e, "confirmation");
            },
            onCancel(){
                console.log("Transaction canceled.")
            }
        })


    }


    return (
        <>
             <button onClick={paystackPay}>
                  Pay {checkoutData.live.subtotal.formatted_with_symbol}
            </button>

            <button
                  onClick={(e) => handleBackStep(e, "order-details")}
                >
                  Back
            </button>
        </>
    )
};



export default Payment;
