import "./style.css";
import { Link } from 'react-router-dom';
import Cart from './Cart';
import CartItem from './CartItem';
import PropTypes from 'prop-types';
import {
    Paper,
    Grid,
    Container,
    LinearProgress,
} from "@mui/material";


const CartView = ({ cart, onRemoveFromCart, onEmptyCart, onUpdateCartQty }) => {

console.log(cart);
  const handleEmptyCart = () => {
    onEmptyCart();
  }

  const renderEmptyMessage = () => {
    if (cart.total_unique_items > 0) {
      return;
    }

    return (
      <p className="cart-none">
        You have no items in your shopping cart, start adding some!
      </p>
    );
  }

  const renderItems = () => (
    cart.line_items.map((lineItem) => (
      <CartItem
        item={lineItem}
        onUpdateCartQty={onUpdateCartQty}
        onRemoveFromCart={onRemoveFromCart}
        key={lineItem.id}
        className="cart-inner"
      />
    ))
  );

  const renderTotal = () => (
    <div className="cart-total">
      <h6 className="cart-total-title">Subtotal</h6>
      <p className="cart-total-price">{cart.subtotal.formatted_with_symbol}</p>
    </div>
  );


if (!cart.id){ 

    return (
        <LinearProgress sx={{ color: '#b00000' }} color="inherit" />
    )
}

    return (
        <Container>
            <Paper>
            <div>
                <div className="cart-view">
                { renderEmptyMessage() }
                { renderItems() }
                <a href="/cart-view">View Cart</a>
                <div className="cart-view-footer">
                { renderTotal() }
                    <button className="cart-btn-empty" onClick={handleEmptyCart}>Clear Cart</button>
                    <Link to="/checkout" className="cart-btn-checkout">Checkout</Link> 
                </div>
                </div>
            </div>
            </Paper>
        </Container>
    )  

};

Cart.propTypes = {
    cart: PropTypes.object,
    onEmptyCart: () => {},
};



export default CartView;
