import { useState, useEffect } from "react";
import commerce from "../../lib/commerce";
import Loader from "../loader/Loader";
import { Grid, Container } from '@mui/material';
import "./style.css";

import ShoppingBagOutlinedIcon from '@mui/icons-material/ShoppingBagOutlined';
// import ShoppingBagSharpIcon from '@mui/icons-material/ShoppingBagSharp';

const createMarkup = (text) => {
    return { __html: text };
  };

const ProductView = ({ addToCart }) => {

    const [product, setProduct] = useState({});
    const [quantity, setQuantity] = useState(1);
    const [loading, setLoading] = useState(true);

    const fetchProduct = async (id) => {
      const response = await commerce.products.retrieve(id);
      const { name, price, image, quantity, description } = response;

      setProduct({
        id,
        name,
        quantity,
        description,
        src: image.url,
        price: price.formatted_with_symbol,
      });
    };

    useEffect(() => {
      const id = window.location.pathname.split("/");
      fetchProduct(id[2]);
    }, []);

    const handleQuantity = (param) => {
      if (param === "decrease" && quantity > 1) {
        setQuantity(quantity - 1);
      }
      if (param === "increase" && quantity < 10) {
        setQuantity(quantity + 1);
      }
    };


    return (
        <Container className='product-view'>
            <Grid container spacing={4}>
                <Grid item xs={12} md={6}>
                <img className="product-view-img"
                    onLoad={() => {
                    setLoading(false);
                    }}
                    src={product.src}
                    alt={product.name}
                />
                </Grid>
                <Grid item xs={12} md={6}>
                <span className="product-view-title">{product.name}</span>
                <p className="product-description"
                    dangerouslySetInnerHTML={createMarkup(product.description)}
                />
                <p className="product-price">{product.price}</p>
                <Grid container spacing={4}>
                    <Grid item xs={12} md={1}>
                    <button
                        className="increase-product-quantity"
                        onClick={() => {
                        handleQuantity("increase");
                        }}
                    >
                        +
                    </button>
                    </Grid>
                    <Grid item xs={12} md={1}>
                    <p className="quantity" variant="h3">
                        {quantity}
                    </p>
                    </Grid>
                    <Grid item xs={12} md={1}>
                    <button
                        onClick={() => {
                        handleQuantity("decrease");
                        }}
                    >
                        -
                    </button>
                    </Grid>
                    <Grid item xs={12}>
                    <button
                        className="product-view-cart-icon"
                        onClick={() => {
                        addToCart(product.id, quantity);
                        }}
                    >
                        <ShoppingBagOutlinedIcon />
                    </button>
                    </Grid>
                </Grid>
                </Grid>
            </Grid>
            {loading && <Loader />}
        </Container>
    );
};


export default ProductView;
